import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Importing Data for eFapsApp-Sales");

SearchQuery query = new SearchQuery();
query.setQueryTypes("Admin_User_JAASKey");
query.addWhereExprEqValue("Key", "Sales_Admin");
query.addSelect("OID");
query.execute();
if (!query.next()) {

    RunLevel.init("shell");
    RunLevel.execute();

    URL url1 = new URL(EFAPS_ROOTURL, "Data/IMPORT_Currency.xml");
    DataImport imp1 = DataImport.readFile(url1);
    imp1.updateInDB();

    URL url2 = new URL(EFAPS_ROOTURL, "Data/IMPORT_Taxes.xml");
    DataImport imp2 = DataImport.readFile(url2);
    imp2.updateInDB();

    URL url3 = new URL(EFAPS_ROOTURL, "Data/IMPORT_UserJAASKey.xml");
    DataImport imp3 = DataImport.readFile(url3);
    imp3.updateInDB();

    URL url4 = new URL(EFAPS_ROOTURL, "Data/IMPORT_Attributes.xml");
    DataImport imp4 = DataImport.readFile(url4);
    imp4.updateInDB();
}
