import java.io.File;
import java.util.UUID;
import org.efaps.admin.runlevel.RunLevel;
import org.efaps.admin.user.JAASSystem;
import org.efaps.admin.user.Role;
import org.efaps.db.Context;
import org.efaps.db.QueryBuilder;
import org.efaps.db.InstanceQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;

EFAPS_LOGGER.info("Importing Data for eFapsApp-Sales");

RunLevel.init("shell");
RunLevel.execute();

// Admin_User_JAASKey
QueryBuilder queryBldr = new QueryBuilder(UUID.fromString("0e7650c6-8ec3-4c63-b377-f3eb5fb85f16"));
queryBldr.addWhereAttrEqValue("Key", "Sales_Admin");
InstanceQuery query = queryBldr.getQuery();
query.executeWithoutAccessCheck();
if (!query.next()) {
    DataImport.readFile(new URL(EFAPS_ROOTURL, "Data/IMPORT_UserJAASKey.xml")).updateInDB();
}

//Sales_TaxCategory
queryBldr = new QueryBuilder(UUID.fromString("25b22ae7-c9d8-4211-832d-06c00b9de709"));
queryBldr.addWhereAttrEqValue("UUID", "ed28d3c0-e55d-45e5-8025-e48fc989c9dd");
query = queryBldr.getQuery();
query.executeWithoutAccessCheck();
if (!query.next()) {
    DataImport.readFile(new URL(EFAPS_ROOTURL, "Data/IMPORT_Currency.xml")).updateInDB();
    DataImport.readFile(new URL(EFAPS_ROOTURL, "Data/IMPORT_Taxes.xml")).updateInDB();
}

